/* 
    CONVERT
    NUM: VALORE, STR: NOME VALUTA PARTENZA, STR: NOME VALUTA FINALE
*/

const currencies = {
    "USD": 1,
    "EUR": 0.9366,
    "INR": 83.264001,
    "AUD": 1.5589,
    "JPY": 150.4210,
    "GBP": 0.8138,
    "CHF": 0.9007
}

exports.convert = (value, startCurrency, endCurrency) => {
    if(!Object.keys(currencies).includes(startCurrency) || !Object.keys(currencies).includes(endCurrency)) return "Wrong currencies name passed!";
    return value / currencies[startCurrency] * currencies[endCurrency];
}